function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   var requestedFor = new GlideAjax('x_832171_lva.LoanerAjax'); // HelloWorld is the script include class 
requestedFor.addParam('sysparm_name','locationDetail'); // helloWorld is the script include method 
requestedFor.addParam('sysparm_user_name', g_form.getValue("requested_for")); // Set parameter sysparm_user_name to 'Bob' 
requestedFor.getXML(setlocation);  /* Call HelloWorld.helloWorld() with the parameter sysparm_user_name set to 'Bob' 
      and use the callback function HelloWorldParse() to return the result when ready */

// the callback function for returning the result from the server-side code
//function office_location(response) {
function setlocation(response) {  
   var answer = response.responseXML.documentElement.getAttribute("answer"); 
    g_form.setValue('location', answer);
}
}
