sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	var brandNew = current.vehicle_status;
	var ngr = new GlideRecord ('x_832171_lva_loaner_vehicle_catalog');

	ngr.addQuery('sys_id',current.vehicle);
	ngr.query();

	if (ngr.next()) {

		if(brandNew == 'Pending Release' || brandNew == 'Out on Field' || brandNew == 'Returned for Inspection' || brandNew == 'Sent for Servicing'){
			  //ngr.setValue('status','unavailable');
			ngr.status = 'Unavailable';
	}
	else
	if(brandNew == 'Returned to Warehouse'){
			ngr.status = 'Available';
			  //ngr.setValue('status', 'available');
	}
	else
	if(brandNew == 'Decommissioned'){
			ngr.status = 'Decommissioned';
			  //ngr.setValue('status', 'decommissioned');
	}
		ngr.update();

	}

})(current, previous);

//1. Create a business rule on the Vehicle Tracker record that will set the status field of the VEHICLE on the Vehicle Tracker record.
//* If the Vehicle Status on the Vehicle Tracker is Pending Release, Out on Field, Returned for Inspection or Sent for Servicing the status of the associated Vehicle should be Unavailable.
//* If the Vehicle Status on the Vehicle Tracker is Returned to Warehouse, set the status field of the Vehicle to Available
//* If the Vehicle Status on the Vehicle Tracker is Decommissioned, set the status field of the Vehicle to Decommissioned
